import React from 'react';

const defaultAvatar = "https://ui-avatars.com/api/?background=random";

export default function Message({ msg, onReactClick }) {
  return (
    <div className="message-bubble fade-in">
      <img className="avatar" src={msg.avatarUrl || defaultAvatar} alt="user avatar" />
      <div>
        <div style={{ fontWeight: 'bold' }}>{msg.user}</div>
        {msg.reply && <div style={{ fontStyle: 'italic', color: '#444', margin: '6px 0' }}>↪️ Reply: {msg.reply}</div>}

        {msg.fileName ? (
          <div>
            <strong>File:</strong> {msg.fileName}
            <div>
              {msg.fileType && msg.fileType.startsWith('image/') ? (
                <img src={msg.data} alt={msg.fileName} style={{ maxWidth: 250, marginTop: 5 }} />
              ) : (
                <a href={msg.data} download={msg.fileName}>Download</a>
              )}
            </div>
          </div>
        ) : (
          <div>{msg.text}</div>
        )}

        <div className="message-status">
          {(msg.status === 'delivered' || msg.status === 'read') && (
            <span style={{ color: msg.status === 'read' ? '#2563eb' : 'gray' }}>
              ✓✓
            </span>
          )}
        </div>

        {msg.reactions?.length > 0 && (
          <div style={{ marginTop: '5px' }}>
            Reactions:{' '}
            {msg.reactions.map((emoji, idx) => (
              <span key={emoji + idx} style={{ marginRight: 6 }}>{emoji}</span>
            ))}
          </div>
        )}

        <button onClick={onReactClick} style={{ marginTop: 5 }}>
          React
        </button>
      </div>
    </div>
  );
}
