import React from 'react';

export default function Message({ msg, onReactClick }) {
  return (
    <div style={{ borderBottom: '1px solid #eee', padding: '8px 0' }}>
      <div>
        <strong>{msg.user}</strong>:
        {msg.reply && (
          <div style={{ fontStyle: 'italic', fontSize: 12, margin: '2px 0' }}>
            ↪️ Reply: {msg.reply}
          </div>
        )}
        <span> {msg.text}</span>
      </div>

      {msg.reactions && msg.reactions.length > 0 && (
        <div style={{ marginTop: 5 }}>
          Reactions:{' '}
          {msg.reactions.map((emoji, idx) => (
            <span key={emoji + idx} style={{ marginRight: 6 }}>
              {emoji}
            </span>
          ))}
        </div>
      )}

      <button onClick={onReactClick} style={{ marginTop: 4 }}>
        React
      </button>
    </div>
  );
}
