// src/components/Message.js
import React from 'react';
const defaultAvatar = "https://ui-avatars.com/api/?background=random";

export default function Message({ msg, onReactClick }) {
  return (
    <div className="message-bubble fade-in">
      <img className="avatar" src={msg.avatarUrl || defaultAvatar} alt="user" />
      <div>
        <div style={{ fontWeight: 'bold', fontSize: 15 }}>{msg.user}</div>
        {msg.reply && (
          <div style={{ fontStyle: 'italic', fontSize: 12, color: "#444", margin: '5px 0' }}>
            ↪️ Reply: {msg.reply}
          </div>
        )}
        <span> {msg.text}</span>
        <div className="message-status">
          {msg.status === 'sent' && <span>✓</span>}
          {msg.status === 'delivered' && <span>✓✓</span>}
          {msg.status === 'read' && <span style={{ color: '#2563eb' }}>✓✓</span>}
        </div>
        {msg.reactions && msg.reactions.length > 0 && (
          <div style={{ marginTop: "4px" }}>
            Reactions:{' '}
            {msg.reactions.map((emoji, idx) => (
              <span key={emoji + idx} style={{ marginRight: 6 }}>
                {emoji}
              </span>
            ))}
          </div>
        )}
        <button onClick={onReactClick} style={{ marginTop: 4 }}>
          React
        </button>
      </div>
    </div>
  );
}
