import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';
import Message from './Message';
import TypingIndicator from './TypingIndicator';
import MessageInput from './MessageInput';
import EmojiPicker from './EmojiPicker';

export default function Chat({ username }) {
  const [messages, setMessages] = useState([]);
  const [typingUser, setTypingUser] = useState('');
  const [userActivity, setUserActivity] = useState('');
  const [activeUsers, setActiveUsers] = useState([]);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  const socketRef = useRef();

  useEffect(() => {
    socketRef.current = io('http://localhost:3001', { query: { username } });

    socketRef.current.on('message', (msg) => setMessages((m) => [...m, msg]));
    socketRef.current.on('userJoined', (msg) => setUserActivity(msg));
    socketRef.current.on('userLeft', (msg) => setUserActivity(msg));
    socketRef.current.on('activeUsers', (users) => setActiveUsers(users));
    socketRef.current.on('typing', (user) => setTypingUser(user));
    socketRef.current.on('reactUpdate', ({ msgId, emoji }) =>
      setMessages((m) =>
        m.map((msg) =>
          msg.id === msgId ? { ...msg, reactions: [...(msg.reactions || []), emoji] } : msg
        )
      )
    );

    return () => socketRef.current.disconnect();
  }, [username]);

  const sendMessage = (text, replyTo) => {
    if (replyTo) {
      socketRef.current.emit('replyMessage', { replyTo, message: text });
    } else {
      socketRef.current.emit('sendMessage', { id: Date.now(), text, user: username });
    }
  };

  const handleTyping = () => {
    socketRef.current.emit('typing', username);
  };

  const reactToMsg = (msgId, emoji) => {
    socketRef.current.emit('reactMessage', msgId, emoji);
  };

  const handleEmojiSelect = (emoji) => {
    if (messages.length > 0) {
      const lastMsgId = messages[messages.length - 1].id;
      reactToMsg(lastMsgId, emoji.native);
    }
    setShowEmojiPicker(false);
  };

  return (
    <div style={{ width: 400, border: '1px solid #ccc', padding: 10 }}>
      <div>
        <h4>Active Users ({activeUsers.length}):</h4>
        <ul>
          {activeUsers.map((user, i) => (
            <li key={i}>{user}</li>
          ))}
        </ul>
      </div>

      <div>{userActivity}</div>

      <MessageInput sendMessage={sendMessage} handleTyping={handleTyping} />

      <button onClick={() => setShowEmojiPicker(!showEmojiPicker)}>
        {showEmojiPicker ? 'Close Emoji Picker' : 'Open Emoji Picker'}
      </button>

      {showEmojiPicker && <EmojiPicker onSelect={handleEmojiSelect} />}

      <TypingIndicator typingUser={typingUser} />

      <div>
        {messages.map((msg) => (
          <Message key={msg.id} msg={msg} reactToMsg={reactToMsg} sendReply={sendMessage} />
        ))}
      </div>
    </div>
  );
}
