import React, { useState, useEffect } from 'react';
import EmojiPicker from './EmojiPicker';

export default function Message({ msg, isReplying, setReplyingTo, sendReply, reactToMsg }) {
  const [replyText, setReplyText] = useState('');
  const [showEmojis, setShowEmojis] = useState(false);

  useEffect(() => {
    if (!isReplying) {
      setReplyText(''); // reset input when reply is closed
    }
  }, [isReplying]);

  const handleReplySubmit = e => {
    e.preventDefault();
    if (replyText.trim()) {
      sendReply(replyText, msg.id);
      setReplyText('');
      setReplyingTo(null);
    }
  };

  return (
    <div style={{ borderBottom: '1px solid #eee', margin: '4px 0', padding: '4px 0' }}>
      <strong>{msg.user}</strong>:
      {msg.reply && (
        <div style={{ fontStyle: 'italic', fontSize: 12, margin: '2px 0' }}>
          ↪️ Reply: {msg.reply}
        </div>
      )}
      <span> {msg.text}</span>

      {/* Show reactions */}
      {msg.reactions && msg.reactions.map((e, i) => <span key={e + i} style={{ marginLeft: 5 }}>{e}</span>)}

      <div style={{ marginTop: 4 }}>
        <button onClick={() => setReplyingTo(isReplying ? null : msg.id)} style={{ marginRight: 8 }}>
          {isReplying ? 'Cancel' : 'Reply'}
        </button>
        <button onClick={() => setShowEmojis(!showEmojis)}>
          {showEmojis ? 'Close React' : 'React'}
        </button>
      </div>

      {showEmojis && (
        <EmojiPicker
          onSelect={emoji => {
            reactToMsg(msg.id, emoji.native);
            setShowEmojis(false);
          }}
        />
      )}

      {isReplying && (
        <form onSubmit={handleReplySubmit} style={{ marginTop: 8 }}>
          <input
            type="text"
            value={replyText}
            onChange={e => setReplyText(e.target.value)}
            placeholder="Type your reply"
            autoFocus
            style={{ width: '80%', padding: 4, marginRight: 4 }}
          />
          <button type="submit">Send</button>
        </form>
      )}
    </div>
  );
}
